<input type="text" id="vm_name" placeholder="vm_name"></input> 
  <input type="text" id="ip" placeholder="ip"></input> 
  <input type="text" id="memory_mb" placeholder="memory_mb"></input> 
  <input type="text" id="num_cpus"  placeholder="num_cpus"></input> 
</div>  
        <h1>Create Redis DB</h1>

        {/* {this.state.currentCount} /  */}
        {/* <form  class="col-6"> */}
        <p aria-live="polite">Current Status: <strong>{this.state.currentstatus.toString()}</strong></p>
        {/* <div>
          <label for="ip" class="form-label">ip : 10.100.65.210</label>
          <input type="text" class="form-control" id="ip"   ></input> 
        </div> */}
        <div>
          <label for="redisport" >redisport :  7001</label>
          <input type="text" id="redisport"></input> 
          <input type="text" id="nodetimeout"></input> 
          <input type="text" id="maxclients"></input> 
          <input type="text" id="maxmemory"></input> 
          <input type="text" id="requirepass"></input> 
        </div>
